<script>
import Vue from "vue";
import ProductSwiper from "@C/ProductSwiper";
import PriceSlice from "@C/PriceSlice";
import GoodsSpec from "@C/GoodsSpec";
import CouponList from "@C/CouponList";
import GoodsLike from "@C/GoodsLike";
import recommend from "@C/Recommend"
import {
    getGoodsDetail,
    getGoodsCoupon,
    addCart,
} from "@/api/store";
import { handleCollectGoods, getCoupon } from "@API/user";
import { getAppDownload } from "@API/app";
import { appSource } from "@U/utils";
import { mapState, mapActions } from "vuex";
const CollectType = {
    COLLECTION: 1,
    CANCEL_COLLECTION: 0,
};
export default {
    name: "goodsDetail",
    components: {
        ProductSwiper,
        PriceSlice,
        GoodsSpec,
        CouponList,
        GoodsLike,
        recommend
    },
    data() {
        return {
            posterpopup: false,
            shareImg: "",
            timeData: {},
            swiperList: [],
            goodsDetail: {},
            checkedGoods: {},
            couponLists: [],
            goodsLike: [],
            isFirstLoading: true,
            showSpec: false,
            popupType: "",
            countTime: 0,
            activity: {},
            comment: {},
            showCoupons: false,
            isFromApp: this.$route.query.isapp,
            showAnim: false,
            iconShaking: false,
            emptyDetails: false
        };
    },

    async created() {
        this.id = this.$route.query.id;
        this.$getGoodsDetail();
        this.$getCoupons();
        this.getCartNum()
    },
    mounted() {},
    beforeDestroy() {
        clearTimeout(this.timer1);
        clearTimeout(this.timer2);
    },
    methods: {
        ...mapActions(['getCartNum']),
        $getAppDownload() {
            getAppDownload({ client: appSource() == "ios" ? 3 : 4 }).then(
                (res) => {
                    if (res.code == 1) {
                        window.location = res.data.line;
                    }
                }
            );
        }, 
        goPage(name, query) {
            this.$router.push({
                name,
                query,
            });
        },
        
        $getGoodsDetail() {
            getGoodsDetail({
                id: this.id,
            }).then((res) => {
                if (res.code == 1) {
                    let {
                        goods_image,
                        content,
                        comment,
                        like,
                    } = res.data;
                    this.goodsDetail = res.data;
                    this.swiperList = goods_image;
                    this.goodsLike = like;
                    this.comment = comment;
                    this.isFirstLoading = false;
                } else {
                    this.emptyDetails = true;
                }
            });
        },
        $getPoster() {
            this.$toast({
                type: "loading",
                message: "正在生成中...",
                duration: 0,
            });
            getPoster({
                id: this.id,
                url: `/mobile/common/goodsdetail?id=${this.id}`,
            }).then((res) => {
                this.$toast.clear();
                if (res.code == 1) {
                    this.shareImg = res.data;
                    this.posterpopup = true;
                }
            });
        },
        $getCoupons() {
            getGoodsCoupon({
                id: this.id,
            }).then((res) => {
                if (res.code == 1) {
                    this.couponLists = res.data;
                }
            });
        },
        userReceiveCoupon(id) {
            getCoupon({ id: id }).then((res) => {
                if (res.code == 1) {
                    this.$toast({ message: res.msg });
                    this.$getCoupons();
                }
            });
        },
        $handleCollectGoods() {
            let { is_collect, id } = this.goodsDetail;
            if (is_collect == CollectType.COLLECTION) {
                is_collect = CollectType.CANCEL_COLLECTION;
            } else {
                is_collect = CollectType.COLLECTION;
            }
            handleCollectGoods({ is_collect: is_collect, goods_id: id }).then(
                (res) => {
                    if (res.code == 1) {
                          if(is_collect == 1) {
                             this.$toast({ message: "收藏成功"});
                        }else {
                              this.$toast({ message: "取消收藏" });
                        }
                        this.$getGoodsDetail();
                    }
                }
            );
        },
        onShowSpec(type) {
            this.popupType = type;
            this.showSpec = true;
        },
        onChangeSpec(e) {
            this.checkedGoods = e;
        },
        onAddCart(e) {
            let { id, goodsNum } = e;
            addCart({
                item_id: id,
                goods_num: goodsNum,
            }).then((res) => {
                if (res.code == 1) {
                    this.iconShaking = false;
                    this.showSpec = false;
                    this.onControlAnim();
                    this.getCartNum()
                }
            });
        },
        onBuyNow(e) {
            let { id, goodsNum } = e;
            let goods = [
                {
                    item_id: id,
                    num: goodsNum,
                },
            ];
            this.showSpec = false;
            this.$nextTick(() => {
                this.$router.push({
                    name: "confirmOrder",
                    query: {
                        goods: JSON.stringify(goods),
                    },
                });
            });
        },
        // 控制动画方法
        onControlAnim() {
            const goodsBall = this.$refs.goodsBall;
            const innerBall = goodsBall.children[0];
            this.showAnim = true;
        },
        afterEnter(el) {
            this.showAnim = false;
        },
        afterLeave(el) {
            // 动画结束后
            this.iconShaking = true;
        },
    },
    computed: {
        ...mapState(['cartNum'])
    },
};
</script>
